

puzzle_input = ""
sample_input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"

def find_marker(signal, size):
    for idx in range(size, len(signal,)):
        slice = signal[idx-size: idx]
        if len(set(slice)) == size:
            return idx

start_packet_size = 4
start_marker_size = 14
assert find_marker("bvwbjplbgvbhsrlpgdmjqwftvncz", start_packet_size) == 5
assert find_marker("nppdvjthqldpwncqszvftbrmjlhg", start_packet_size) == 6
assert find_marker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", start_packet_size) == 10
assert find_marker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", start_packet_size) == 11


print("Part 1:", find_marker(puzzle_input, start_packet_size))
print("Part 2:", find_marker(puzzle_input, start_marker_size))

